import React from "react";
import "./Hotel.css";
import foto1 from "./foto1.png";
import { Card, Button, Container, Col } from "react-bootstrap";

function Hotel(props) {
  return (
    <div className="container border">
      <div className="row py-3 bg-light justify-content-center">
        <div className="col-md-3">
          <img src="https://picsum.photos/200" alt="hotel img" />
        </div>
        <div className="col-md-7 fs-4">
          <div className="row">
            <h5 className="card-title">
              {props.name} - {props.rate}
            </h5>
          </div>
          <div className="row">
            <p className="card-text fw-light">{props.location}</p>
          </div>
          <div className="row">
            <p className="card-text text-end">{props.days}</p>
          </div>
          <div className="row">
            <p className="card-text text-end">
              {props.oceanview == true ? "oceanview" : ""} {props.roomtype}{" "}
              {props.mealtype}
            </p>
          </div>
          <div className="row">
            <h6 className="card-text fw-bold text-end fs2">{props.price}</h6>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Hotel;
